import React, {Component} from 'react';
import '../index.css';
import raw from './credentials.txt';

import { useState } from "react";
import Axios from "axios";

class Library extends Component{
    constructor(props) {
        super(props)
        let lines = []
        fetch(raw)
            .then(r => r.text())
                .then(text => {
                    lines.push(text.split("\n"));
        });
        this.state = {
            lines,
            newAccount: false,
            content: true,
            showImg: false,
            path: "",
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleNewAccount = this.handleNewAccount.bind(this);
        this.signOut = this.signOut.bind(this);
        this.addBook = this.addBook.bind(this);
        this.loadPic = this.loadPic.bind(this);
    }

    render() {
        const createNewAccount = (this.state.newAccount) ? this.createNewAccount(): null;
        const content = (this.state.content) ? this.content(): null;
        return (
            <div>
                <br/>
                {this.login()}
                <div id = "loginContent">
                    {content}
                    {createNewAccount}
                </div>

            </div>
        );
    }

    toggle() {
        this.setState({newAccount: !this.state.newAccount});
        this.setState({content: !this.state.content});
        try {
            document.getElementById("accPass").value = "";
            document.getElementById("newU").value = "";
            document.getElementById("newP").value = "";
            document.getElementById("confirmP").value = "";
        }
        catch(e) {

        };
    }

    content() {
        const image = (this.state.showImg) ? (<img src= {this.state.path} className = "previewImg"></img>): null;
        const closeBtn = (this.state.showImg) ? (<button className = "button2" onClick = {(e) => this.closePic(e)}>Close Picture</button>): null;
        const [bTitle, setbTitle] = useState("");
        const [bAuthor, setbAuthor] = useState("");
        const [bookList, setbookList] = useState([]);

        const addBook = () => {
            console.log(btitle);
            console.log(bauthor);

        Axios.post('http://localhost:3001/create', {
        btitle: bTitle,
        bauthor: bAuthor,
        }).then( 
            (response) => { console.log(response) },
            (error) => { console.log(error) }
        );

        };

        return (
            <div className = "Content">
                <h1 className = "header1">Enter Book Information:</h1>
                <form>
                    <div className = "col1">
                        <div style = {{padding: "5vh"}}>
                            <div>
                                <h3>General Info:</h3>
                                <label>
                                    <input className = "addBookInputs" type="text" id = "bTitle" 
                                    placeholder = "Name" 
                                    onChange={(event) => {
                                        setbTitle(event.target.value);
                                      }}
                                      required="required"/>
                                </label>
                                <label>
                                    <input className = "addBookInputs" type="text" id = "bAuthor" 
                                    placeholder = "Author" 
                                    onChange={(event) => {
                                        setbAuthor(event.target.value);
                                    }} 
                                    required="required"/>
                                </label>
                                <label>
                                    <input className = "addBookInputs" type="text" id = "bPublisher" placeholder = "Publisher" required="required"/>
                                </label>
                                <label>
                                    <input className = "addBookInputs" type="text" id = "bDate" placeholder = "Published Date" required="required"/>
                                </label>
                                <label>
                                    <input className = "bookSummary" type="textarea" id = "bSummary" placeholder = "Summary" required="required"/>
                                </label>
                            </div>
                            <br/>
                            <div>
                                <h3>Additional Info:</h3>
                                <p>*This information is not required, but is necessary for the website to recommend book.*</p>
                                <h4>Genre</h4>
                                <div>
                                    <label>
                                        Action:   
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Adventure:
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Comedy:   
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Mystery:  
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Fantasy:  
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Horror:   
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Sci-Fi:   
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Thriller: 
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                    <label>
                                        Romance:  
                                        <input type="checkbox" id = "bAdventure"/>
                                    </label>
                                </div>
